import React from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'


class UserShow extends React.Component {
    constructor(){
        super()
        this.state = {
            user: {},
            post: []
        }
    }

    componentDidMount() {
        const id = this.props.match.params.id
        axios.get(`http://jsonplaceholder.typicode.com/users/${id}`)
        .then((response) => {
            console.log(response.data)
            const user = response.data
            this.setState({user})
            const title = this.props.match.params.title
            axios.get(`http://jsonplaceholder.typicode.com/posts/${title}`)
            .then((response) => {
            const post = response.data
            this.setState({post})
        })
    })
    }

    render(){
        console.log('user show component', this.props )

        return(
            <div>
                <h1> USER NAME: {this.state.user.username} </h1>
                <h2> POSTS WRITTEN BY USER </h2>
                <p> {this.state.post.title} 
                <Link to={`/posts/${post.title}`}></Link></p>
            </div>
        )
    }
}
export default UserShow
