import React from 'react'
import axios from 'axios'

class PostShow extends React.Component {
    constructor(){
        super()
        this.state = {
            post: {},
            user: {},
            comments: []
        }
    }

    componentDidMount() {
        const id = this.props.match.params.id
        axios.get(`http://jsonplaceholder.typicode.com/posts/id`)
        axios.get(`http://jsonplaceholder.typicode.com/users/id`)
        axios.get(`http://jsonplaceholder.typicode.com/comments?posts/id`)

        .then((response) => {
            console.log(response.data)
            const post = response.data
            this.setState({post})
            const user = response.data
            this.setState({user})
            const comments = response.data
            this.setState({comments})
        })
    }

    render(){
        console.log('user show component', this.props )

        return(
            <div>
                <h1> USER NAME: {this.state.user.username} </h1>
                <h2> POSTS WRITTEN BY USER {this.state.post.title}</h2>
                <p> {this.state.posts} </p>
            </div>
        )
    }
}
export default PostShow
